package Patterns;

public class Pat22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4,i,j,k,k1;
		for (i = 0; i <n; i++) 
        { 
            for (j = 0; j <= i; j++) 
            { 	
            	
            		System.out.print("c");
            }
           
            		for( k=0;k<=i;k++)
            		{
            			System.out.print("s");
            		}
            		for( k1=0;k1<=i;k1++)
            		{
            			System.out.print("k");
            		}
            		System.out.println();
			}
			

	}

}
