package Patterns;

public class Pat8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int al=65;
		for (int i = 0; i<=4; i++) {

			for (int j = 0; j<=i; j++) {
				
				System.out.printf("%c",al);
				al++;

			}
			System.out.println();
		}


	}

}
