package Series;

public class Ser2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=6;
				int c=0;
		for(int i=1;i<n;i++)
		{
			
           if(i%2!=0)
           {
			 c=(i*i)-1;
           System.out.println(c);
           }
           else
           {
        	   c=(i*i)-2;
           System.out.println(c);
           }
			//System.out.println(i);
			
			
			
		}
		
	}

}
